import React from 'react';
import Profile from "./Profile";
import axios from "axios";
import {connect} from "react-redux";
import {setUserDescription, setUserFullName, setUserProfile} from "../../Redax/Profile-reducer";
import {useParams} from 'react-router-dom'

export function withRouter(Children){
    return(props)=>{
        const match  = {params: useParams()};
        return <Children {...props}  match = {match}/>
    }
}

class ProfileContainer extends React.Component {

    componentDidMount() {
        debugger;
        axios.get(`https://social-network.samuraijs.com/api/1.0/profile/2`)
            .then(response => {
                this.props.setUserProfile(response.data)
                this.props.setUserFullName(response.data.fullName)
                this.props.setUserDescription(response.data.aboutMe)
            });
    }

    render() {
        return (
            <div>
                <Profile {...this.props}
                         profile={this.props.profile}
                         fullName={this.props.fullName}
                         description={this.props.description}

                />
            </div>
        )
    }
}

let mapStateToProps = (state) => ({
    profile: state.profilePage.profile,
    fullName: state.profilePage.fullName,
    description: state.profilePage.description,
})



export default connect(mapStateToProps, {
    setUserProfile,
    setUserFullName,
    setUserDescription
})(ProfileContainer);
