        //     fullName: 'Angelika Lema',
        //     status: "Redhead girl",
        //     location:
        //         {
        //             city: 'Wroclaw',
        //             country: 'Poland',
        //
        //         },
        // },
        // {
        //     id: 3,
        //     followed: false,
        //     photoURL:'https://i.pinimg.com/originals/eb/42/b3/eb42b30af8dbbee64166eed52e37c87c.png',
        //     fullName: 'Aneta Snihur',
        //     status: "Manchester City Fan №1",
        //     location:
        //         {
        //             city: 'Wroclaw',
        //             country: 'Poland',
        //
        //         },
        // },
    ],
    pageSize: 100,
    totalUsersCount: 1,
    currentPage: 1,
    isFetching: true,
    followingInProgress: [],
}

const UsersReducer = (state = initialState, action) => {
    switch (action.type) {
        case FOLLOW: {
            return {
                ...state,
                users: state.users.map(user => {
                    if (user.id === action.userID) {
                        return {...user, followed: true}
                    }
                    return user;
                })
            }
        }
        case UNFOLLOW: {
            return {
                ...state,
                users: state.users.map(user => {
                    if (user.id === action.userID) {
                        return {...user, followed: false}
                    }
                    return user;
                })
            }
        }
        case SET_USERS: {
            return {...state, users: action.users
            }
        }
        case SET_CURRENT_PAGE: {
            return {...state,
                currentPage: action.currentPage
            }
        }
        case SET_TOTAL_USERS_COUNT: {
            return {...state,
                totalUsersCount: action.totalUsersCount
            }
        }
        case TOGGLE_IS_FETCHING: {
            return {
                ...state,
                isFetching: action.isFetching
            }
        }
        case TOGGLE_IS_FOLLOWING_IN_PROGRESS: {
            return {
                ...state,
                followingInProgress: [...state,followingInProgress.filter()]
            }
        }
        default:
            return state;
    }
}


export const follow = (userID) => ({type: FOLLOW, userID});
export const unfollow = (userID) => ({type: UNFOLLOW, userID})
export const setUsers = (users) => ({type: SET_USERS, users})
export const setCurrentPage = (currentPage) => ({type: SET_CURRENT_PAGE, currentPage})
export const setTotalUsersCount = (totalUsersCount) => ({type: SET_TOTAL_USERS_COUNT, totalUsersCount})
export const toggleIsFetching = (isFetching) => ({type: TOGGLE_IS_FETCHING, isFetching})
export const toggleFollowingProgress = (isInProgress) => ({type: TOGGLE_IS_FOLLOWING_IN_PROGRESS, isInProgress})



export default UsersReducer;
