import React from 'react';
import s from './Dialogs.module.css';
import {Field, reduxForm} from "redux-form";

const MyDialogForm = (props) => {
    return (
        <form onSubmit={props.onSubmit}>
            <Field
                    name={'dialogTextArea'}
                    component={'textarea'}
                   placeholder={"Enter your message"}
                   onChange={props.onMassageChange}
                   value={props.newMessageText}/>
            <div>
                <button type='submit' onClick={props.onAddMessage}>Add Message</button>
                <button type='submit'>Remove</button>
            </div>
        </form>
    )
}

const RedaxDialogForm = reduxForm({form: 'dialog'})(MyDialogForm)

const DialogsForm = (props) => {
    const onSubmit = (formData) => {
        console.log(formData);
    }
    return (
        <div className={s.postBlock}>
            <h5> Enter Message </h5>
            <RedaxDialogForm onSubmit={onSubmit}
                             onAddMessage={props.onAddMessage}
                             onMassageChange={props.onMassageChange}
                             newMessageText={props.newMessageText}
            />
        </div>

    )
}

export default DialogsForm;
