let state = {
    profilePage: {
        posts: [
            {id: 1, message: "Hi, How are you", like: 15},
            {id: 2, message: "Hi, it's my first post", like: 10},
            {id: 3, message: "412412", like: 123},
            {id: 4, message: "ohohohooh", like: 1123},
        ],
        newPostText: "Hi, How are you",
    },
    dialogsPage: {
        messages: [
            {id: 1, message: "Hi"},
            {id: 2, message: "How is your day?"},
            {id: 3, message: "You"},
            {id: 4, message: "You"},
            {id: 5, message: "You"},
        ],
        dialogs: [
            {id: 1, name: "Max"},
            {id: 2, name: "Angelika"},
            {id: 3, name: "Aneta"},
            {id: 4, name: "Zoja"},
            {id: 5, name: "Andjei"},
            {id: 6, name: "Marysia"},
        ],
        newMessageText: "Hi, How are you",
    },
}

window.state = state;

export const addPost = () => {
    let newPost = {
        id: 5,
        message: state.profilePage.newPostText,
        like: 0,
    };
    state.profilePage.posts.push(newPost);
    state.profilePage.newPostText = "";
    rerenderEntireTreePererisovka(state);
};

export const updateNewPostChange = (newText) => {
    state.profilePage.newPostText = newText;
    rerenderEntireTreePererisovka(state);
};

export const addMessage = () => {
    let newMessage = {
        id: 6,
        message: state.dialogsPage.newMessageText,
    };
    state.dialogsPage.messages.push(newMessage);
    state.dialogsPage.newMessageText = "";
    rerenderEntireTreePererisovka(state);
};

export const updateNewMessageChange = (newMessage) => {
    state.dialogsPage.newMessageText = newMessage;
    rerenderEntireTreePererisovka(state);
};

let rerenderEntireTreePererisovka = () => {};

export const subscribe = (observer) => {
    rerenderEntireTreePererisovka = observer;
}

export default state;
