import profileReducer from "./Profile-reducer";
import dialogsReducer from "./Dialogs-reducer";
import sidebarReducer from "./Sidebar-reducer";


const ADD_POST = 'ADD_POST';
const UPDATE_NEW_POST_CHANGE = 'UPDATE_NEW_POST_CHANGE';
const ADD_MESSAGE= "ADD_MESSAGE";
const UPDATE_NEW_MESSAGE_CHANGE = "UPDATE_NEW_MESSAGE_CHANGE"


let store = {
    _state: {
        profilePage: {
            posts: [
                {id: 1, message: "Hi, How are you", like: 15},
                {id: 2, message: "Hi, it's my first post", like: 10},
                {id: 3, message: "412412", like: 123},
                {id: 4, message: "ohohohooh", like: 1123},
            ],
            newPostText: "Hi, How are you",
        },
        dialogsPage: {
            messages: [
                {id: 1, message: "Hi"},
                {id: 2, message: "How is your day?"},
                {id: 3, message: "You"},
                {id: 4, message: "You"},
                {id: 5, message: "You"},
            ],
            dialogs: [
                {id: 1, name: "Max"},
                {id: 2, name: "Angelika"},
                {id: 3, name: "Aneta"},
                {id: 4, name: "Zoja"},
                {id: 5, name: "Andjei"},
                {id: 6, name: "Marysia"},
            ],
            newMessageText: "",
        },
        sidebar: {},
    },
    "_rerenderEntireTreePererisovka"() {
        console.log("ebobo")
    },

    "getState"() {
        return this._state;
    },
    "subscribe"(observer)  {
        this._rerenderEntireTreePererisovka = observer;
    },

    "dispatch"(action) {

        profileReducer(this._state.profilePage, action);
        dialogsReducer(this._state.dialogsPage, action);
        sidebarReducer(this._state, action);




        if (action.type === ADD_POST) {
            let newPost = {
                id: 5,
                message: this._state.profilePage.newPostText,
                like: 0,
            };
            this._state.profilePage.posts.push(newPost);
            this._state.profilePage.newPostText = "";
            this._rerenderEntireTreePererisovka(this._state);
        } else if (action.type === UPDATE_NEW_POST_CHANGE) {
            this._state.profilePage.newPostText = action.newText;
            this._rerenderEntireTreePererisovka(this._state);
        } else if (action.type === ADD_MESSAGE) {
            let newMessage = {
                id: 6,
                message: this._state.dialogsPage.newMessageText,
            };
            this._state.dialogsPage.messages.push(newMessage);
            this._state.dialogsPage.newMessageText = "";
            this._rerenderEntireTreePererisovka(this._state);
        } else if (action.type === UPDATE_NEW_MESSAGE_CHANGE) {
            this._state.dialogsPage.newMessageText = action.newMessage;
            this._rerenderEntireTreePererisovka(this._state);
        }
    },
}

export const addPostActionCreator = () => ({type: ADD_POST})
export const updateNewPostChangeActionCreator = (text) =>
    ({type: UPDATE_NEW_POST_CHANGE, newText: text})
export const addMessageActionCreator = () => ({type: ADD_MESSAGE})
export const updateNewMessageChangeActionCreator = (text) =>
    ({type: UPDATE_NEW_MESSAGE_CHANGE, newMessage: text})

export default store;

