import React, {useEffect} from 'react';
import Profile from "./Profile";
import axios from "axios";
import {connect} from "react-redux";
import {profileInfo} from "../../Redax/Profile-reducer";
import {useParams} from 'react-router-dom'

function ProfileContainer(props) {
    let {userId} = useParams();
    if (!userId) {
        userId = 2;
    }

    useEffect(() => {
        // props.profileInfo(userId);
        axios
            .get(`https://social-network.samuraijs.com/api/1.0/profile/` + userId)
            .then((response) => {
                props.setUserProfile(response.data)
                props.setUserFullName(response.data.fullName)
                props.setUserDescription(response.data.aboutMe)
            });
    }, [userId]);

    return (
        <div>
            <Profile profile={props.profile}
                     fullName={props.fullName}
                     description={props.description}/>
        </div>
    );
}

let mapStateToProps = (state) => ({
    profile: state.profilePage.profile,
    fullName: state.profilePage.fullName,
    description: state.profilePage.description,
})

export default connect(mapStateToProps, {
    profileInfo,
})(ProfileContainer);
