// }

let objOfDispatch = {
    follow,
    unfollow,

    setUsers,
    setCurrentPage,
    setTotalUsersCount,
    toggleIsFetching,

    toggleFollowingProgress,

    getUsers: getUsersThunkCreator,
}

export default connect(mapStateToProps, objOfDispatch)(UsersContainer);
