const ADD_MESSAGE = "ADD_MESSAGE";
const UPDATE_NEW_MESSAGE_CHANGE = "UPDATE_NEW_MESSAGE_CHANGE"

let initialState = {
    messages: [
        {id: 1, message: "Hi"},
        {id: 2, message: "How is your day?"},
        {id: 3, message: "You"},
        {id: 4, message: "You"},
        {id: 5, message: "You"},
    ],
    dialogs: [
        {id: 1, name: "Max"},
        {id: 2, name: "Angelika"},
        {id: 3, name: "Aneta"},
        {id: 4, name: "Zoja"},
        {id: 5, name: "Andjei"},
        {id: 6, name: "Marysia"},
    ],
    newMessageText: "",
}

const dialogsReducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_MESSAGE: {
            let newMessage = {
                id: 6,
                message: state.newMessageText,
            };
            return {
                ...state,
                newMessageText: "",
                messages: [...state.messages, newMessage]
            };
        }
        case UPDATE_NEW_MESSAGE_CHANGE: {
            return {
                ...state,
                newMessageText: action.newMessage,
            };
        }
        default:
            return state;
    }
}

export const addMessageActionCreator = () => ({type: ADD_MESSAGE})
export const updateNewMessageChangeActionCreator = (text) =>
    ({type: UPDATE_NEW_MESSAGE_CHANGE, newMessage: text})


export default dialogsReducer;
