const ADD_POST = 'ADD_POST';
const UPDATE_NEW_POST_CHANGE = 'UPDATE_NEW_POST_CHANGE';
const SET_USER_PROFILE = 'SET_USER_PROFILE';
const SET_USER_FULLNAME='SET_USER_FULLNAME';
const SET_USER_DESCRIPTION='SET_USER_DESCRIPTION';

let initialState = {
    posts: [
        {id: 1, message: "Hi, How are you", like: 15},
        {id: 2, message: "Hi, it's my first post", like: 10},
        {id: 3, message: "412412", like: 123},
        {id: 4, message: "ohohohooh", like: 1123},
    ],
    newPostText: "Hi, How are you",
    profile: null,
    fullName: "FULLNAMNE",
    description: "DESCRIPTION",

};

const profileReducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_POST: {
            let newPost = {
                id: 5,
                message: state.newPostText,
                like: 0,
            };
            return {
                ...state,
                newPostText: "",
                posts: [...state.posts, newPost]
            };
        }
        case UPDATE_NEW_POST_CHANGE: {
            return {
                ...state,
                newPostText: action.newText,
            };
        }
        case SET_USER_PROFILE: {
            return {
                ...state,
                profile: action.profile,
            };
        }
        case SET_USER_FULLNAME: {
            return {
                ...state,
                fullName: action.fullName,
            };
        }
        case SET_USER_DESCRIPTION: {
            return {
                ...state,
                description: action.description,
            };
        }

        default:
            return state;
    }
}

export const addPostActionCreator = () => ({type: ADD_POST})
export const updateNewPostChangeActionCreator = (text) =>
    ({type: UPDATE_NEW_POST_CHANGE, newText: text})

export const setUserProfile = (profile) => ({type: SET_USER_PROFILE, profile})
export const setUserFullName = (fullName) => ({type: SET_USER_FULLNAME, fullName})
export const setUserDescription = (description) => ({type: SET_USER_DESCRIPTION, description})

export default profileReducer;
