import React from 'react';
import s from './Dialogs.module.css';
import Message from "./Message/Message";
import DialogItem from "./DialogItem/DialogItem";
import {addMessageActionCreator, updateNewMessageChangeActionCreator} from "../../Redax/State";

const Dialogs = (props) => {
    let dialogsElements = props.dialogs.map((d) => <DialogItem name={d.name} id={d.id}/>)
    let messageElements = props.messages.map((m, index) => <Message massage={m.message}/>)

    let newMessageElement = React.createRef();

    let addMessage = () => {
        props.dispatch(addMessageActionCreator());
    };

    let onMassageChange = (event) => {
        
        let text = newMessageElement.current.value;
        let action = updateNewMessageChangeActionCreator(text);
        props.dispatch(action);
    };

    return (
        <div className={s.dialogs}>
            <div className={s.dialogItems}>
                {dialogsElements}
            </div>
            <div className={s.messages}>
                <div>{messageElements}</div>
                <textarea placeholder="Enter your message"
                          onChange={onMassageChange}
                          ref={newMessageElement}
                          value={props.newMessageText}/>
                <div>
                    <button onClick={addMessage}>Add Message</button>
                    <button>Remove</button>
                </div>
            </div>
        </div>
    )
};

export default Dialogs;
