import React from 'react';
import s from './Dialogs.module.css';
import Message from "./Message/Message";
import DialogItem from "./DialogItem/DialogItem";
import {Field, reduxForm} from "redux-form";

const Dialogs = (props) => {
    let dialogsElements = props.dialogs.map((d) => <DialogItem name={d.name} key={d.id} id={d.id}/>)
    let messageElements = props.messages.map((m, index) => <Message massage={m.message} key={m.id}/>)

    let onAddMessage = () => {
        props.addMessage();
    };

    let onMassageChange = (event) => {
        let text = event.target.value;
        props.updateNewMessageChange(text);
    };
};

const MyDialogForm = (props) => {
    return (
        <form onSubmit={props.onSubmit}>
            <Field component={'textarea'}
                   name={'postTextArea'}
                   placeholder={"Enter your message"}
                   onChange={props.onMassageChange}
                   value={props.newMessageText}/>
            <div>
                <button type='submit' onClick={props.onAddMessage}>Add Message</button>
                <button type='submit'>Remove</button>
            </div>
        </form>
    )
}

const RedaxDialogForm = reduxForm({form: 'login'})(MyDialogForm)

const DialogsForm = (props) => {
    const onSubmit = (formData) => {
        console.log(formData);
    }
    return (
        <div className={s.postBlock}>
            <h3> My post </h3>
            <RedaxDialogForm onSubmit={onSubmit}
                             onAddMessage={props.onAddMessage}
                             onMassageChange={props.onMassageChange}
                             newMessageText={props.newMessageText}
            />
        </div>

    )
}

export default DialogsForm;
