import React from 'react';
import s from './MyPosts.module.css';
import Post from './Post/Post';
import {addPostActionCreator, updateNewPostChangeActionCreator} from "../../../Redax/Profile-reducer";

const MyPosts = (props) => {

    let postsElement = props.posts.map((p) => <Post message={p.message} like={p.like}/>)
    let newPostElement = React.createRef();

    let onAddPost = () => {
        // props.addPost();
        props.dispatch(addPostActionCreator());
    };

    let onPostChange = () => {
        let text = newPostElement.current.value;
        // props.updateNewPostChange(text);
        let action = updateNewPostChangeActionCreator(text);
        props.dispatch(action);
    };

    return (
        <div className={s.postBlock}>
            <h3> My post </h3>
            <div>
                <div>
                    <textarea onChange={onPostChange}
                              ref={newPostElement}
                              value={props.newPostText}/>
                </div>
                <div>
                    <button onClick={onAddPost}>Add post</button>
                    <button>Remove</button>
                </div>
            </div>
            <div className={s.posts}>
                {postsElement}
            </div>
        </div>
    )
}

export default MyPosts;
