import React from 'react';
import s from './MyPosts.module.css';
import AddPost from "./AddPost/AddPost";
import {Field, reduxForm} from "redux-form";
import {maxLengthCreator, required} from "../../../Utils/Validators/validators";
import {Textarea} from "../../Common/FormsControls/FormsControls";

const MyPosts =React.memo(props => {
    console.log("Render");
    let postsElement = props.posts.map((p) => <AddPost message={p.message} like={p.like} key={p.id}/>)

    let onAddPost = (values) => {
        props.addPost(values.postTextArea);
    };

    return (
        <div className={s.postBlock}>
            <h3> My post </h3>
            <PostFormRedux onSubmit={onAddPost}/>
            <div className={s.posts}>
                {postsElement}
            </div>
        </div>
    )
});

const PostForm = (props) => {
    return (
        <form onSubmit={props.handleSubmit}>
            <div>
                <Field name={'postTextArea'}
                       component={Textarea}
                       placeholder={"Hi, how are you"}
                       validate={[required, maxLengthCreator(10)]}/>
            </div>
            <div>
                <button type='submit'>Add post</button>
                <button type='submit'>Remove</button>
            </div>
        </form>
    )
}


const PostFormRedux = reduxForm({form: "post"})(PostForm)

export default MyPosts;

