import React from 'react';
import {addMessageActionCreator, updateNewMessageChangeActionCreator} from "../../Redax/Dialogs-reducer";
import Dialogs from "./Dialogs";
import StoreContext from "../../StoreContext";

const DialogsContainer = () => {

    // let state = props.store.getState().dialogsPage;
    //
    // let addMessage = () => {
    //     props.store.dispatch(addMessageActionCreator());
    // };
    //
    // let onMassageChange = (text) => {
    //     let action = updateNewMessageChangeActionCreator(text);
    //     props.store.dispatch(action);
    // };

    return (
        <StoreContext.Consumer> {
            (store) => {
                let state = store.getState().dialogsPage;
                let addMessage = () => {
                    store.dispatch(addMessageActionCreator());
                };
                let onMassageChange = (text) => {
                    let action = updateNewMessageChangeActionCreator(text);
                    store.dispatch(action);
                };

                return (
                    <Dialogs
                        addMessage={addMessage}
                        updateNewMessageChange={onMassageChange}
                        newMessageText={state.newMessageText}
                        dialogs={state.dialogs}
                        messages={state.messages}/>)
            }
        }
        </StoreContext.Consumer>
    )
};

export default DialogsContainer;
